mapper.update:
update browse set browse_time=SYSDATE()
where browse_user_id=#{browse_user_id} and browse_data_id=#{browse_data_id}
and browse_identification=#{browse_identification}

mapper.select:
select * from collection
where collection_user_ids=#{wx_id}
and collection_user_id=#{wx_ids}

select * from collection where collection_user_id =
#{user_id} and
collection_user_ids = #{id}

choose.when:
DELETE from collection
where
collection_user_id=#{wx_ids} and collection_user_ids=#{wx_id}

mapper.select:
select goods_id,goods_vetype,goods_length,goods_name,goods_number, (SELECT IF(TO_DAYS(now()) >= TO_DAYS(goods_time), ( SELECT DATE_FORMAT( g.goods_time, '%m-%d %H:%i' ) ), ( SELECT DATE_FORMAT( g.goods_time, '%Y-%m-%d' ) ) ))as goods_time, goods_start,goods_end,goods_contacts,goods_contactinformation,goods_user_id, (SELECT IF(TO_DAYS(now()) >= TO_DAYS(goods_loadingtime), ( SELECT DATE_FORMAT( g.goods_loadingtime, '%m-%d %H:%i' ) ), ( SELECT DATE_FORMAT( g.goods_loadingtime, '%Y-%m-%d' ) ) )) AS goods_loadingtime, user_id,wx_headimgurl, wx_name,wx_nickname from goods g join user u on g.goods_user_id=u.user_id WHERE 1 = 1
AND goods_start LIKE CONCAT('%',#{param1.goods_start_area},'%')
AND goods_end LIKE CONCAT('%',#{param1.goods_end_area},'%')
and (goods_number = 0
or goods_number BETWEEN #{item.min_number} AND #{item.max_number}
or goods_number >= #{item.min_number}
)
and (goods_loadingtime = "1999-09-09"
/** *查询今天的 */
or to_days(goods_loadingtime) = to_days(now())
/** *查询明天的 */
or TO_DAYS(goods_loadingtime)-TO_DAYS(NOW())=1
/** *查询明天以后的 */
or TO_DAYS(goods_loadingtime)>TO_DAYS(NOW())
)
AND goods_vehicletype LIKE CONCAT('%',#{param1.goods_vehicletype},'%')
AND goods_length LIKE CONCAT('%',#{param1.goods_length},'%')
and (goods_vetype = "成功"
or goods_vetype LIKE CONCAT('%',#{item},'%')
)
AND goods_type LIKE CONCAT('%',#{param1.goods_type},'%')
ORDER BY goods_loadingtime DESC

select vehicle_id,vehicle_start,vehicle_end,vehicle_contacts,vehicle_contactinformation,vehicle_length, vehicle_type,(SELECT IF(TO_DAYS(now()) >= TO_DAYS(vehicle_releasetime), ( SELECT DATE_FORMAT( v.vehicle_releasetime, '%m-%d %H:%i' ) ), ( SELECT DATE_FORMAT( v.vehicle_releasetime, '%Y-%m-%d' ) ) ))as vehicle_releasetime,vehicle_user_id,vehicle_number,user_id,wx_headimgurl, wx_name,wx_nickname from vehicle v join user u on v.vehicle_user_id=u.user_id WHERE 1 = 1
AND vehicle_start LIKE CONCAT('%',#{param1.vehicle_start_area},'%')
AND vehicle_end LIKE CONCAT('%',#{param1.vehicle_end_area},'%')
and (vehicle_number = 0
or vehicle_number BETWEEN #{item.min_number} AND #{item.max_number}
or vehicle_number >= #{item.min_number}
)
AND vehicle_length LIKE CONCAT('%',#{param1.vehicle_length},'%')
and (vehicle_type = "成功"
or vehicle_type LIKE CONCAT('%',#{item},'%')
)
ORDER BY vehicle_releasetime DESC

