mapper.update:
update admin_user set user_password = #{user_password}
where user_name =
#{user_name}

mapper.select:
select count(*) as
order_vehicle from vehicle where vehicle_releasetime like
#{dates}

select count(*) as vehicle_number from vehicle WHERE vehicle_pa=0

	and vehicle_releasetime > #{startTime}


	and vehicle_releasetime < #{endTime}

select count(*) as goods_number from goods WHERE goods_pa=0

	and goods_time > #{startTime}


	and goods_time < #{endTime}
